import React, { useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000';

function App() {
  const [folderName, setFolderName] = useState('');
  const [files, setFiles] = useState([]);
  const [fileList, setFileList] = useState([]);
  const [prompt, setPrompt] = useState('');
  const [message, setMessage] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [editContent, setEditContent] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  // Handle folder upload
  const handleFolderUpload = async (e) => {
    e.preventDefault();
    if (!folderName || files.length === 0) {
      setMessage('Please provide a folder name and select files.');
      return;
    }
    const formData = new FormData();
    formData.append('folderName', folderName);
    for (let file of files) {
      formData.append('files', file);
    }
    try {
      await axios.post(`${API_URL}/upload-folder`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setMessage('Folder uploaded successfully.');
      fetchFileList();
    } catch (err) {
      setMessage('Error uploading folder.');
    }
  };

  // Fetch file list
  const fetchFileList = async () => {
    if (!folderName) return;
    try {
      const res = await axios.get(`${API_URL}/list-files`, { params: { folderName } });
      setFileList(res.data.files);
    } catch {
      setFileList([]);
    }
  };

  // Handle prompt actions (simple parser: create, edit, delete)
  const handlePrompt = async () => {
    if (!prompt.trim() || !folderName) return;
    const [action, ...rest] = prompt.trim().split(' ');
    const fileName = rest[0];
    const content = rest.slice(1).join(' ');
    if (action === 'create' && (!fileName || fileName.trim() === '')) {
      setMessage('Please provide a file name for creation.');
      return;
    }
    try {
      if (action === 'create') {
        await axios.post(`${API_URL}/create-file`, { folderName, fileName, content });
        setMessage(`File ${fileName} created.`);
      } else if (action === 'edit') {
        await axios.post(`${API_URL}/edit-file`, { folderName, fileName, content });
        setMessage(`File ${fileName} edited.`);
      } else if (action === 'delete') {
        await axios.post(`${API_URL}/delete-file`, { folderName, fileName });
        setMessage(`File ${fileName} deleted.`);
      } else {
        setMessage('Unknown command. Use: create, edit, or delete.');
        return;
      }
      fetchFileList();
    } catch (err) {
      setMessage('Operation failed.');
    }
  };


  const openEditModal = async (file) => {
    setSelectedFile(file);
    // Fetch file content
    try {
      const res = await axios.get(`${API_URL}/list-files`, { params: { folderName } });
      setEditContent('');
    } catch {
      setEditContent('');
    }
    setShowEditModal(true);
  };


  const handleEditSubmit = async () => {
    try {
      await axios.post(`${API_URL}/edit-file`, { folderName, fileName: selectedFile, content: editContent });
      setMessage(`File ${selectedFile} edited.`);
      setShowEditModal(false);
      fetchFileList();
    } catch {
      setMessage('Failed to edit file.');
    }
  };


  const openDeleteConfirm = (file) => {
    setSelectedFile(file);
    setShowDeleteConfirm(true);
  };

 
  const handleDelete = async () => {
    try {
      await axios.post(`${API_URL}/delete-file`, { folderName, fileName: selectedFile });
      setMessage(`File ${selectedFile} deleted.`);
      setShowDeleteConfirm(false);
      fetchFileList();
    } catch {
      setMessage('Failed to delete file.');
    }
  };

  return (
    <div style={{ maxWidth: 600, margin: '40px auto', fontFamily: 'sans-serif' }}>
      <h2>MCP Filesystem Manager</h2>
      <form onSubmit={handleFolderUpload}>
        <input
          type="text"
          placeholder="Folder Name"
          value={folderName}
          onChange={e => setFolderName(e.target.value)}
          style={{ marginRight: 8 }}
        />
        <input
          type="file"
          webkitdirectory="true"
          directory="true"
          multiple
          onChange={e => setFiles(Array.from(e.target.files))}
        />
        <button type="submit">Upload Folder</button>
      </form>
      <div style={{ margin: '20px 0' }}>
        <input
          type="text"
          placeholder="Prompt (e.g. create file.txt Hello)"
          value={prompt}
          onChange={e => setPrompt(e.target.value)}
          style={{ width: '70%', marginRight: 8 }}
        />
        <button onClick={handlePrompt}>Run</button>
      </div>
      {message && <div style={{ color: 'green', marginBottom: 10 }}>{message}</div>}
      <h4>Files in Folder:</h4>
      <ul>
        {fileList.map(f => (
          <li key={f} style={{ display: 'flex', alignItems: 'center', marginBottom: 4 }}>
            <span style={{ flex: 1 }}>{f}</span>
            <button style={{ marginRight: 4 }} onClick={() => openEditModal(f)}>Edit</button>
            <button onClick={() => openDeleteConfirm(f)}>Delete</button>
          </li>
        ))}
      </ul>
      <button onClick={fetchFileList}>Refresh File List</button>

      {showEditModal && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ background: '#fff', padding: 20, borderRadius: 8, minWidth: 300 }}>
            <h3>Edit {selectedFile}</h3>
            <textarea
              rows={8}
              style={{ width: '100%' }}
              value={editContent}
              onChange={e => setEditContent(e.target.value)}
            />
            <div style={{ marginTop: 10, textAlign: 'right' }}>
              <button onClick={() => setShowEditModal(false)} style={{ marginRight: 8 }}>Cancel</button>
              <button onClick={handleEditSubmit}>Save</button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirm */}
      {showDeleteConfirm && (
        <div style={{ position: 'fixed', top: 0, left: 0, width: '100vw', height: '100vh', background: 'rgba(0,0,0,0.3)', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ background: '#fff', padding: 20, borderRadius: 8, minWidth: 300 }}>
            <h3>Delete {selectedFile}?</h3>
            <div style={{ marginTop: 10, textAlign: 'right' }}>
              <button onClick={() => setShowDeleteConfirm(false)} style={{ marginRight: 8 }}>Cancel</button>
              <button onClick={handleDelete}>Delete</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
