import React, { useState } from 'react';
import axios from 'axios';

const API_URL = 'http://localhost:5000';

const styles = {
  container: {
    maxWidth: 500,
    margin: '40px auto',
    fontFamily: 'Segoe UI, sans-serif',
    background: '#f9f9fb',
    borderRadius: 16,
    boxShadow: '0 4px 24px rgba(0,0,0,0.08)',
    padding: 32,
  },
  section: {
    marginBottom: 28,
    paddingBottom: 16,
    borderBottom: '1px solid #e0e0e0',
  },
  input: {
    padding: '8px 12px',
    borderRadius: 6,
    border: '1px solid #bbb',
    marginRight: 8,
    fontSize: 15,
  },
  button: {
    padding: '8px 18px',
    borderRadius: 6,
    border: 'none',
    background: '#4f8cff',
    color: '#fff',
    fontWeight: 600,
    cursor: 'pointer',
    marginRight: 6,
    transition: 'background 0.2s',
  },
  buttonDanger: {
    background: '#ff4f4f',
  },
  fileList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
  },
  fileItem: {
    display: 'flex',
    alignItems: 'center',
    marginBottom: 8,
    background: '#fff',
    borderRadius: 6,
    boxShadow: '0 1px 4px rgba(0,0,0,0.04)',
    padding: '8px 12px',
  },
  fileName: {
    flex: 1,
    fontWeight: 500,
    color: '#333',
  },
  modalOverlay: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100vw',
    height: '100vh',
    background: 'rgba(0,0,0,0.18)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1000,
  },
  modal: {
    background: '#fff',
    padding: 28,
    borderRadius: 12,
    minWidth: 320,
    boxShadow: '0 4px 24px rgba(0,0,0,0.12)',
  },
  modalTitle: {
    margin: 0,
    marginBottom: 12,
    fontWeight: 600,
    fontSize: 18,
  },
  textarea: {
    width: '100%',
    borderRadius: 6,
    border: '1px solid #bbb',
    padding: 10,
    fontSize: 15,
    marginBottom: 12,
    minHeight: 100,
    resize: 'vertical',
  },
  message: {
    color: '#2e7d32',
    background: '#e8f5e9',
    borderRadius: 6,
    padding: '8px 12px',
    marginBottom: 10,
    fontWeight: 500,
    fontSize: 15,
  },
};

function App() {
  const [folderName, setFolderName] = useState('');
  const [files, setFiles] = useState([]);
  const [fileList, setFileList] = useState([]);
  const [prompt, setPrompt] = useState('');
  const [message, setMessage] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [editContent, setEditContent] = useState('');
  const [showEditModal, setShowEditModal] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  // Handle folder upload
  const handleFolderUpload = async (e) => {
    e.preventDefault();
    if (!folderName || files.length === 0) {
      setMessage('Please provide a folder name and select files.');
      return;
    }
    const formData = new FormData();
    formData.append('folderName', folderName);
    for (let file of files) {
      formData.append('files', file);
    }
    try {
      await axios.post(`${API_URL}/upload-folder`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      setMessage('Folder uploaded successfully.');
      fetchFileList();
    } catch (err) {
      setMessage('Error uploading folder.');
    }
  };

  // Fetch file list
  const fetchFileList = async () => {
    if (!folderName) return;
    try {
      const res = await axios.get(`${API_URL}/list-files`, { params: { folderName } });
      setFileList(res.data.files);
    } catch {
      setFileList([]);
    }
  };

  // Handle prompt actions (simple parser: create, edit, delete)
  const handlePrompt = async () => {
    if (!prompt.trim() || !folderName) return;
    const [action, ...rest] = prompt.trim().split(' ');
    const fileName = rest[0];
    const content = rest.slice(1).join(' ');
    if (action === 'create' && (!fileName || fileName.trim() === '')) {
      setMessage('Please provide a file name for creation.');
      return;
    }
    try {
      if (action === 'create') {
        await axios.post(`${API_URL}/create-file`, { folderName, fileName, content });
        setMessage(`File ${fileName} created.`);
      } else if (action === 'edit') {
        await axios.post(`${API_URL}/edit-file`, { folderName, fileName, content });
        setMessage(`File ${fileName} edited.`);
      } else if (action === 'delete') {
        await axios.post(`${API_URL}/delete-file`, { folderName, fileName });
        setMessage(`File ${fileName} deleted.`);
      } else {
        setMessage('Unknown command. Use: create, edit, or delete.');
        return;
      }
      fetchFileList();
    } catch (err) {
      setMessage('Operation failed.');
    }
  };

  // UI: Open edit modal and load file content
  const openEditModal = async (file) => {
    setSelectedFile(file);
    // Fetch file content
    try {
      const res = await axios.get(`${API_URL}/list-files`, { params: { folderName } });
      setEditContent('');
    } catch {
      setEditContent('');
    }
    setShowEditModal(true);
  };

  // UI: Handle edit submit
  const handleEditSubmit = async () => {
    try {
      await axios.post(`${API_URL}/edit-file`, { folderName, fileName: selectedFile, content: editContent });
      setMessage(`File ${selectedFile} edited.`);
      setShowEditModal(false);
      fetchFileList();
    } catch {
      setMessage('Failed to edit file.');
    }
  };

  // UI: Open delete confirm
  const openDeleteConfirm = (file) => {
    setSelectedFile(file);
    setShowDeleteConfirm(true);
  };

  // UI: Handle delete
  const handleDelete = async () => {
    try {
      await axios.post(`${API_URL}/delete-file`, { folderName, fileName: selectedFile });
      setMessage(`File ${selectedFile} deleted.`);
      setShowDeleteConfirm(false);
      fetchFileList();
    } catch {
      setMessage('Failed to delete file.');
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={{ textAlign: 'center', marginBottom: 28, color: '#4f8cff', letterSpacing: 1 }}>MCP Filesystem Manager</h2>
      <div style={styles.section}>
        <form onSubmit={handleFolderUpload} style={{ display: 'flex', alignItems: 'center', flexWrap: 'wrap' }}>
          <input
            type="text"
            placeholder="Folder Name"
            value={folderName}
            onChange={e => setFolderName(e.target.value)}
            style={styles.input}
          />
          <input
            type="file"
            webkitdirectory="true"
            directory="true"
            multiple
            onChange={e => setFiles(Array.from(e.target.files))}
            style={styles.input}
          />
          <button type="submit" style={styles.button}>Upload Folder</button>
        </form>
      </div>
      <div style={styles.section}>
        <input
          type="text"
          placeholder="Prompt (e.g. create file.txt Hello)"
          value={prompt}
          onChange={e => setPrompt(e.target.value)}
          style={{ ...styles.input, width: '60%' }}
        />
        <button onClick={handlePrompt} style={styles.button}>Run</button>
      </div>
      {message && <div style={styles.message}>{message}</div>}
      <div style={styles.section}>
        <h4 style={{ margin: '0 0 12px 0', color: '#333' }}>Files in Folder:</h4>
        <ul style={styles.fileList}>
          {fileList.map(f => (
            <li key={f} style={styles.fileItem}>
              <span style={styles.fileName}>{f}</span>
              <button style={styles.button} onClick={() => openEditModal(f)}>Edit</button>
              <button style={{ ...styles.button, ...styles.buttonDanger }} onClick={() => openDeleteConfirm(f)}>Delete</button>
            </li>
          ))}
        </ul>
        <button onClick={fetchFileList} style={{ ...styles.button, marginTop: 10 }}>Refresh File List</button>
      </div>

      {/* Edit Modal */}
      {showEditModal && (
        <div style={styles.modalOverlay}>
          <div style={styles.modal}>
            <h3 style={styles.modalTitle}>Edit {selectedFile}</h3>
            <textarea
              rows={8}
              style={styles.textarea}
              value={editContent}
              onChange={e => setEditContent(e.target.value)}
            />
            <div style={{ marginTop: 10, textAlign: 'right' }}>
              <button onClick={() => setShowEditModal(false)} style={{ ...styles.button, marginRight: 8, background: '#bbb' }}>Cancel</button>
              <button onClick={handleEditSubmit} style={styles.button}>Save</button>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirm */}
      {showDeleteConfirm && (
        <div style={styles.modalOverlay}>
          <div style={styles.modal}>
            <h3 style={styles.modalTitle}>Delete {selectedFile}?</h3>
            <div style={{ marginTop: 10, textAlign: 'right' }}>
              <button onClick={() => setShowDeleteConfirm(false)} style={{ ...styles.button, marginRight: 8, background: '#bbb' }}>Cancel</button>
              <button onClick={handleDelete} style={{ ...styles.button, ...styles.buttonDanger }}>Delete</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
